package com.syntax.class02;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// First 1 way to create variable 
		byte b=10; // we created assign and the value
		
		// Second 2 way to create variable 
		int i; // we created the variable 
		// now how to assign the value 
		i=100; // we assigned the value 
		
		// we need to create a variable only once 
		// then we can use as many times we want or need during the program
		
		// Third 3 way 
		// we created a variable or declare variable 
		int num1, num2, num3; 
		num1=100; // we assigned and initialize variable 
		num2=200; // it called I assigned the value  
		num3=300; 
		
		// can I change the value of variable?
		// Yes I can
		num2=2000; // It called I reassigned the value 
		
		System.out.println(num2); 
		// one variable = one value in one line or one time 
		
		boolean boo=true; 
		boo=false; 
		boo=true; 
		
		
	

	}

}
